const initialState = {
    filteredProducts:[],
    isFreeShipping:true,
    items:[],
    name:''
};

const productReducer = (state = initialState,action)=> {

   // console.log('custom data', state.items);
   // console.log('reducer data', action.data);
    console.log('reducer11111 data',{...state});
    const newItem = {...state}
    switch(action.type){
        case 'FETCH_PRODUCT': 

        
            return {
                ...newItem,
                items:action.data,
                filteredProducts:{...action.data}
            }
            
          
        default:return state;

    }
}
export default productReducer;
