import React, { Component } from 'react';
import axios from 'axios';
import Products from './components/Products';
import Filter from './components/Filter';
import './App.css';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products:[],
      filterProducts:[],
      size:'',
      sort:''
    }
  }
  componentWillMount() {


    fetch('http://localhost:8000/products').then(res => res.json())
      .catch(err => fetch('db.json').then(res => res.json()).then(data => data.products))
      .then(data => {
        console.log('its data here',data);
        this.setState({filterProducts:data});
         console.log('Here will list data',this.state.filterProducts);
        });
  }
  handleChangeSize(e){
    this.setState({size:e.target.value})
  }
  handleSortChange = (e) => {
    this.setState({ sort: e.target.value });
    this.listProducts();
  }
  handleSizeChange = (e) => {
    this.setState({ size: e.target.value });
    this.listProducts();
  }


  listProducts = () => {
    this.setState(state => {
      if (state.sort !== '') {
        state.products.sort((a, b) =>
          (state.sort === 'lowestprice'
            ? ((a.price > b.price) ? 1 : -1)
            : ((a.price < b.price) ? 1 : -1)));
      } else {
        state.products.sort((a, b) => (a.id > b.id) ? 1 : -1);
      }
      if (state.size !== '') {
        return { filteredProducts: state.products.filter(a => a.availableSizes.indexOf(state.size.toUpperCase()) >= 0) };
      }
      return { filteredProducts: state.products };
    })
  }
  handleSortChange = (e) => {
    this.setState({ sort: e.target.value });
    this.listProducts();
  }
  handleSizeChange = (e) => {
    this.setState({ size: e.target.value });
    this.listProducts();
  }

  render() {
    return (
      <div className="container text-center">
        <h1>E-Commerce Shopping cart application</h1>
        <hr />
        <div className="row">
          <div className="col-md-8">
            <Filter size={this.state.size} sort={this.state.sort}  handleSortChange={this.handleSortChange}
              handleChangeSize={this.handleChangeSize} />
            <hr />
            <Products products={this.state.filterProducts} handleAddToCart={this.handleAddToCart} usename="kaleshwar" />
          </div>
          <div className="col-md-4"></div>
        </div>
      </div>
    );
  }
}

export default App;
