import React, { Component } from 'react';
import {filterProducts} from '../actions/index';
import { connect } from "react-redux";
class Filter extends Component {

    render() {


        return (
            <div className="row">
                <div className="col-md-4">
                    {this.props.items.count}
                    {`${this.props.items.count} products found.`}
                </div>
                <div className="col-md-4">
                    <label>Order by
               <select className="form-control" value={this.props.sort} onChange={this.props.handleSortChange}>
                            <option value="">Select</option>
                            <option value="lowestprice">Lowest to highest</option>
                            <option value="highestprice">Highest to lowest</option>
                        </select>
                    </label>
                </div>
                <div className="col-md-4">
                    <label > Filter Size
               <select className="form-control" value={this.props.size} onChange={(e)=> this.props.filterProducts(this.props.items,e.target.value)}>
                            <option value="">ALL</option>
                            <option value="x">XS</option>
                            <option value="s">S</option>
                            <option value="m">M</option>
                            <option value="l">L</option>
                            <option value="xl">XL</option>
                            <option value="xxl">XXL</option>
                        </select>
                    </label>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => {

    return {
        items:state.productReducer.items,
        size:state.productReducer.size
    };
  };
  export default connect(mapStateToProps,{filterProducts})(Filter);

//export default Filter;
