const initialState = {
    filteredProducts:[],
    isFreeShipping:true,
    items:[]
};

const productReducer = (state = initialState,action)=> {

    
    console.log('reducer data', {...action.data});
    console.log('reducer11111 data',{...state,});
    //console.log('custom data', state.items.length);
    switch(action.type){
        case 'FETCH_PRODUCT':{ 
            return {
                ...state,
                items:action.data,
                filteredProducts:action.data
            }
        }
            
          
        default:return state;

    }
}
export default productReducer;
