import React, { Component } from 'react';
import axios from 'axios';
export default class Products extends Component {

    render() {
      if(this.props.product){
        const productItems = this.props.products.map(product => 
           <div className="col-md-4">
             <div className="tumbnail text-center">
                 <a href={`#${product.id}`} onClick={this.handleAddToCart}>
                     <img scr={`products/${product.sku}.jpg`} alt={product.title} />
                 </a>
                 <p>{product.title}</p>
             </div>
           </div>

       )
      }

        return (
            <div className="row">
            {productItems}
            </div>
        )
    }
}
