import React, { Component } from 'react';
import util from '../util';
import {fetchProducts,addToCart} from '../actions/index';
import { connect } from "react-redux";

 class Products extends Component {

    constructor(props) {
        super(props);
      }

    componentWillMount() {

        this.props.fetchProducts();
        console.log('Hello data is loading here');

       if (localStorage.getItem('cartItems')) {
         this.setState({ cartItems: JSON.parse(localStorage.getItem('cartItems')) });
       }
     }

    //  handleRemoveFromCart = (e, product) => {
    //     console.log('Clicked here',product);
    //     const cartItems = this.state.cartItems.filter(a =>a.id!==product.id);
    //     this.setState({cartItems:cartItems});

    //   }


    render() {
       console.log('testing at run time',this.props.products);
        const productItems = this.props.products.map(product => (

            <div className="col-md-4" key={product.id}>
                <div className="thumbnail text-center">
                    <a href={`#${product.id}`} onClick={(e)=> this.props.handleAddToCart(this.props.cartItems, product)}>
                        <img src={`products/${product.sku}_2.jpg`} alt={product.title} />
                        <p>{product.title}</p>                        
                    </a>
                    <b>{util.formatCurrency(product.price)}</b>
                    <button className="btn btn-primary" onClick={(e)=> this.props.handleAddToCart(this.props.cartItems, product)}>Add to cart</button>
                </div>
            </div>
        ));

        return (
            <div className="row">
                {productItems}
            </div>
        )
    }
}

const mapStateToProps = state => {
  console.log('product listing page',state.productReducer.filteredProducts);
  return {
    products:state.productReducer.filteredProducts,
    cartItems:state.productReducer.cartItems    
  };
};

  export default connect(mapStateToProps,{fetchProducts,addToCart})(Products)
