  }

  render() {
    return (
      <div className="container text-center">
        <h1>E-Commerce Shopping cart application</h1>
        <hr />
        <div className="row">
          <div className="col-md-8">
            <Filter size={this.state.size} sort={this.state.sort}  handleSortChange={this.handleSortChange}
              handleSizeChange={this.handleSizeChange} />
            <hr />
            <Products products={this.state.filterProducts} handleAddToCart={this.handleAddToCart} usename="kaleshwar" />
          </div>
          <div className="col-md-4"></div>
        </div>
      </div>
    );
  }
}

export default App;
